import React, { useState } from 'react';
import AddFacultyForm from './AddFacultyForm';
import RemoveFaculty from './RemoveFaculty';

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState('add');

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-white p-5 space-y-4">
        <h2 className="text-2xl font-bold mb-6">Admin Dashboard</h2>
        <button
          onClick={() => setActiveSection('add')}
          className={`block w-full text-left px-4 py-2 rounded hover:bg-gray-700 ${
            activeSection === 'add' ? 'bg-gray-700' : ''
          }`}
        >
          ➕ Add Faculty
        </button>
        <button
          onClick={() => setActiveSection('remove')}
          className={`block w-full text-left px-4 py-2 rounded hover:bg-gray-700 ${
            activeSection === 'remove' ? 'bg-gray-700' : ''
          }`}
        >
          ❌ Remove Faculty
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-8 overflow-y-auto bg-gray-100">
        {activeSection === 'add' && <AddFacultyForm />}
        {activeSection === 'remove' && <RemoveFaculty />}
      </main>
    </div>
  );
};

export default MAinAdminDashboard;
