import React, { useEffect, useState } from 'react';
import './HodDashboard.css'; // Use shared styling

export default function PrincipalDashboard() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/hod/uid-requests')
      .then(res => res.json())
      .then(data => {
        const filtered = data.filter(r => r.hodAccept && r.adminAccept && r.prinAccept !== true);
        setRequests(filtered);
      })
      .catch(err => {
        console.error(err);
        alert('Failed to load data.');
      });
  }, []);

  const handleAction = async (id, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/principal/uid-request/${id}/${status}`, {
        method: 'PUT'
      });
      const data = await res.json();
      alert(data.message);
      setRequests(prev => prev.filter(r => r._id !== id));
    } catch (err) {
      console.error(err);
      alert('Action failed');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h3>Principal Panel</h3>
        <ul className="menu">
          <li className="active">ðŸ“„ Final UID Approval</li>
        </ul>
      </div>

      <div className="main-content">
        <h2>Requests Approved by R&D Admin</h2>
        {requests.length === 0 ? (
          <p>No requests pending final approval.</p>
        ) : (
          requests.map(req => (
            <div className="uid-request-card" key={req._id}>
              <h4>{req.paperTitle}</h4>
              <p><strong>Faculty Name:</strong> {req.facultyName}</p>
              <p><strong>Faculty ID:</strong> {req.facultyId}</p>
              <p><strong>Department:</strong> {req.department}</p>
              <p><strong>Type:</strong> {req.type}</p>
              <p><strong>Target:</strong> {req.target}</p>
              <p><strong>Abstract:</strong> {req.abstract}</p>
              <p><strong>Co-Authors:</strong> {req.coAuthors}</p>
              <p><strong>UID:</strong> {req.uid}</p>
              <p><strong>Submitted On:</strong> {new Date(req.submittedAt).toLocaleDateString()}</p>

              <div className="actions">
                <button className="accept" onClick={() => handleAction(req._id, 'accept')}>Accept</button>
                <button className="reject" onClick={() => handleAction(req._id, 'reject')}>Reject</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
