import React, { useState } from 'react';
import './FacultyDashboard.css';

export default function FacultyDashboard() {
  const [activeSection, setActiveSection] = useState('request-uid');

  const [formValues, setFormValues] = useState({
    paperTitle: '',
    type: '',
    abstract: '',
    target: '',
    department: '',
    coAuthors: ''
  });

  const handleFormChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/hod/uid-request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formValues)
      });

      const data = await res.json();
      alert(data.message);
      setFormValues({
        paperTitle: '',
        type: '',
        abstract: '',
        target: '',
        department: '',
        coAuthors: ''
      });
    } catch (err) {
      console.error(err);
      alert('Submission failed');
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo-section">
          <img src="/svecw-logo.png" alt="SVECW Logo" className="logo" />
        </div>
        <nav className="menu">
          <p>Faculty Login</p>
          <ul>
            <li className={activeSection === 'request-uid' ? 'active' : ''} onClick={() => setActiveSection('request-uid')}>üìÑ Request UID</li>
            <li className={activeSection === 'uid-status' ? 'active' : ''} onClick={() => setActiveSection('uid-status')}>üìò UID Status</li>
            <li className={activeSection === 'submission' ? 'active' : ''} onClick={() => setActiveSection('submission')}>üìù Publication Submission</li>
            <li className={activeSection === 'indexing' ? 'active' : ''} onClick={() => setActiveSection('indexing')}>üì§ Submit Indexing Proof</li>
            <li className={activeSection === 'my-submissions' ? 'active' : ''} onClick={() => setActiveSection('my-submissions')}>üìÇ My Submissions</li>
            <li className={activeSection === 'incentive' ? 'active' : ''} onClick={() => setActiveSection('incentive')}>üí∞ Incentive Status</li>
            <li className={activeSection === 'notifications' ? 'active' : ''} onClick={() => setActiveSection('notifications')}>üîî Notifications</li>
            <li className={activeSection === 'summary' ? 'active' : ''} onClick={() => setActiveSection('summary')}>üìä Publication Summary</li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h3>Welcome, Faculty Name</h3>
        <div className="summary-cards">
          <div className="card">2<br /><span>Approved PIDs</span></div>
          <div className="card">3<br /><span>Incentives Pending</span></div>
        </div>

        {/* UID Request Form */}
        {activeSection === 'request-uid' && (
          <div className="centered-form-container">
            <div className="uid-form-card">
              <h3>Request UID</h3>
              <form onSubmit={handleFormSubmit}>
                <input
                  type="text"
                  name="paperTitle"
                  placeholder="Tentative Paper Title"
                  value={formValues.paperTitle}
                  onChange={handleFormChange}
                  required
                />
                <select name="type" value={formValues.type} onChange={handleFormChange} required>
                  <option value="">Type (Conference/Journal)</option>
                  <option value="Conference">Conference</option>
                  <option value="Journal">Journal</option>
                </select>
                <textarea
                  name="abstract"
                  placeholder="Abstract Space of 150 Words"
                  rows="3"
                  value={formValues.abstract}
                  onChange={handleFormChange}
                  required
                />
                <input
                  type="text"
                  name="target"
                  placeholder="Targeted Journal/ Conference Name"
                  value={formValues.target}
                  onChange={handleFormChange}
                  required
                />
                <select name="department" value={formValues.department} onChange={handleFormChange} required>
                  <option value="">Department</option>
                  <option value="CSE">CSE</option>
                  <option value="ECE">ECE</option>
                  <option value="EEE">EEE</option>
                  <option value="IT">IT</option>
                  <option value="MECH">MECH</option>
                </select>
                <input
                  type="text"
                  name="coAuthors"
                  placeholder="Co-Authors (if any)"
                  value={formValues.coAuthors}
                  onChange={handleFormChange}
                />
                <button type="submit">Submit to HoD</button>
              </form>
            </div>
          </div>
        )}

        {/* Other Sections */}
        {activeSection === 'uid-status' && <p>UID Status Component</p>}
        {activeSection === 'submission' && <p>Publication Submission Component</p>}
        {activeSection === 'indexing' && <p>Submit Indexing Proof Component</p>}
        {activeSection === 'my-submissions' && <p>My Submissions Component</p>}
        {activeSection === 'incentive' && <p>Incentive Status Component</p>}
        {activeSection === 'notifications' && <p>Notifications Component</p>}
        {activeSection === 'summary' && <p>Publication Summary Component</p>}
      </div>
    </div>
  );
}
