import React, { useEffect, useState } from 'react';
import './UIDStatusList.css';

export default function UIDStatusList({ facultyId }) {
  const [requests, setRequests] = useState([]);
  const [rejected, setRejected] = useState([]);
  const [tab, setTab] = useState('approved');

  useEffect(() => {
    const fetchData = async () => {
      const res = await fetch(`http://localhost:5000/api/faculty/uid-requests/${facultyId}`);
      const data = await res.json();
      setRequests(data);
    };

    const fetchRejected = async () => {
      const res = await fetch(`http://localhost:5000/api/faculty/rejected-uids/${facultyId}`);
      const data = await res.json();
      setRejected(data);
    };

    fetchData();
    fetchRejected();
  }, [facultyId]);

  const approved = requests.filter(r => r.hodAccept && r.principalAccept && r.adminAccept && r.uid);
  const pending = requests.filter(r => !r.hodAccept || !r.principalAccept || !r.adminAccept);

  const getCards = (list) =>
    list.map((req, i) => (
      <div className="uid-request-card" key={i}>
        <h4>{req.paperTitle}</h4>
        <p><strong>Target:</strong> {req.target}</p>
        <p><strong>Type:</strong> {req.type}</p>
        <p><strong>Status:</strong> {tab === 'approved' ? `✅ UID: ${req.uid}` : tab === 'pending' ? '⏳ Pending' : '❌ Rejected'}</p>
        {tab === 'rejected' && req.reason && (
          <p><strong>Reason:</strong> {req.reason}</p>
        )}
      </div>
    ));

  return (
    <div>
      <div className="status-buttons">
        <button className={tab === 'approved' ? 'active' : ''} onClick={() => setTab('approved')}>✅ Approved UIDs</button>
        <button className={tab === 'pending' ? 'active' : ''} onClick={() => setTab('pending')}>⏳ Pending UIDs</button>
        <button className={tab === 'rejected' ? 'active' : ''} onClick={() => setTab('rejected')}>❌ Rejected UIDs</button>
      </div>

      {tab === 'approved' && getCards(approved)}
      {tab === 'pending' && getCards(pending)}
      {tab === 'rejected' && getCards(rejected)}
    </div>
  );
}
