import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

export default function FacultyLogin() {
  const [facultyId, setFacultyId] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/faculty/login', {
        facultyId,
        password
      });

      alert(response.data.message);

      // ✅ Store facultyId in localStorage for future use
      localStorage.setItem('facultyId', response.data.faculty.facultyId);

      // Redirect to faculty dashboard
      navigate('/faculty-dashboard');
    } catch (error) {
      if (error.response && error.response.data) {
        alert(error.response.data.message);
      } else {
        alert('Login failed. Please try again.');
      }
    }
  };

  return (
    <div style={styles.container}>
      <h2>Faculty Login</h2>
      <form onSubmit={handleLogin} style={styles.form}>
        <input
          type="text"
          placeholder="Faculty ID"
          value={facultyId}
          onChange={(e) => setFacultyId(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Log in</button>
        <div style={styles.options}>
          <label>
            <input type="checkbox" /> Remember Me
          </label>
          <Link to="#" style={styles.link}>Forgot Password?</Link>
        </div>
        <p>Don't have an account? <Link to="/register">Sign up</Link></p>
      </form>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '400px',
    margin: '50px auto',
    textAlign: 'center',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '10px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  input: {
    padding: '10px',
    fontSize: '16px'
  },
  button: {
    padding: '10px',
    backgroundColor: '#007BFF',
    color: 'white',
    fontWeight: 'bold',
    border: 'none',
    cursor: 'pointer'
  },
  options: {
    display: 'flex',
    justifyContent: 'space-between',
    fontSize: '14px'
  },
  link: {
    textDecoration: 'none'
  }
};
