import React, { useState } from 'react';
import axios from 'axios';

const MainAdminDashboard = () => {
  const [activeSection, setActiveSection] = useState('addFaculty');
  const [facultyData, setFacultyData] = useState({
    fullName: '',
    email: '',
    password: '',
    facultyId: '',
    phoneNumber: '',
    gender: '',
    department: ''
  });
  const [facultyIdToRemove, setFacultyIdToRemove] = useState('');

  const handleAddFaculty = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/main-admin/add-faculty', facultyData);
      alert('Faculty added successfully');
      setFacultyData({
        fullName: '',
        email: '',
        password: '',
        facultyId: '',
        phoneNumber: '',
        gender: '',
        department: ''
      });
    } catch (err) {
      alert(err.response?.data?.message || 'Error adding faculty');
    }
  };

  const handleRemoveFaculty = async (e) => {
    e.preventDefault();
    try {
      await axios.delete(`http://localhost:5000/main-admin/remove-faculty/${facultyIdToRemove}`);
      alert('Faculty removed successfully');
      setFacultyIdToRemove('');
    } catch (err) {
      alert(err.response?.data?.message || 'Error removing faculty');
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* Left Sidebar */}
      <div className="w-64 bg-gray-800 text-white p-5 space-y-4">
        <h2 className="text-xl font-bold mb-6">Admin Dashboard</h2>
        <button
          onClick={() => setActiveSection('addFaculty')}
          className="w-full text-left py-2 px-4 rounded hover:bg-gray-700"
        >
          ‚ûï Add Faculty
        </button>
        <button
          onClick={() => setActiveSection('removeFaculty')}
          className="w-full text-left py-2 px-4 rounded hover:bg-gray-700"
        >
          üóëÔ∏è Remove Faculty
        </button>
      </div>

      {/* Right Content Area */}
      <div className="flex-1 p-10 bg-gray-100">
        {activeSection === 'addFaculty' && (
          <form onSubmit={handleAddFaculty} className="space-y-4 max-w-xl">
            <h2 className="text-2xl font-semibold mb-4">Add Faculty</h2>
            {['fullName', 'email', 'password', 'facultyId', 'phoneNumber', 'gender', 'department'].map((field) => (
              <input
                key={field}
                type={field === 'password' ? 'password' : 'text'}
                placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
                value={facultyData[field]}
                onChange={(e) => setFacultyData({ ...facultyData, [field]: e.target.value })}
                className="w-full p-2 border rounded"
                required
              />
            ))}
            <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
              Add Faculty
            </button>
          </form>
        )}

        {activeSection === 'removeFaculty' && (
          <form onSubmit={handleRemoveFaculty} className="space-y-4 max-w-md">
            <h2 className="text-2xl font-semibold mb-4">Remove Faculty</h2>
            <input
              type="text"
              placeholder="Enter Faculty ID"
              value={facultyIdToRemove}
              onChange={(e) => setFacultyIdToRemove(e.target.value)}
              className="w-full p-2 border rounded"
              required
            />
            <button type="submit" className="bg-red-600 text-white px-4 py-2 rounded">
              Remove Faculty
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default MainAdminDashboard;
