import React, { useEffect, useState } from 'react';

export default function FacultyDashboard() {
  const [approvedRequests, setApprovedRequests] = useState([]);
  const facultyId = localStorage.getItem('facultyId');

  useEffect(() => {
    const fetchApprovedRequests = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/faculty/approved-uid-requests/${facultyId}`);
        const data = await response.json();
        setApprovedRequests(data);
      } catch (error) {
        console.error('Error fetching approved UID requests:', error);
      }
    };

    if (facultyId) {
      fetchApprovedRequests();
    }
  }, [facultyId]);

  const handleFileUpload = async (e, requestId) => {
    e.preventDefault();
    const formData = new FormData();
    const acceptanceLetter = e.target.acceptanceLetter.files[0];
    const indexingProof = e.target.indexingProof.files[0];

    if (acceptanceLetter) formData.append('acceptanceLetter', acceptanceLetter);
    if (indexingProof) formData.append('indexingProof', indexingProof);

    try {
      const response = await fetch(`http://localhost:5000/api/faculty/upload-documents/${requestId}`, {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      alert(result.message);
    } catch (error) {
      console.error('Error uploading documents:', error);
      alert('Upload failed');
    }
  };

  return (
    <div>
      <h2>Approved UID Requests</h2>
      {approvedRequests.map((request) => (
        <div key={request._id} className="uid-request-card">
          <h3>{request.paperTitle}</h3>
          <p><strong>Type:</strong> {request.type}</p>
          <p><strong>Abstract:</strong> {request.abstract}</p>
          <p><strong>Target:</strong> {request.target}</p>
          <p><strong>Co-Authors:</strong> {request.coAuthors}</p>
          <form onSubmit={(e) => handleFileUpload(e, request._id)}>
            <div>
              <label>Acceptance Letter:</label>
              <input type="file" name="acceptanceLetter" accept=".pdf,.doc,.docx" required />
            </div>
            <div>
              <label>Indexing Proof:</label>
              <input type="file" name="indexingProof" accept=".pdf,.doc,.docx" required />
            </div>
            <button type="submit">Upload Documents</button>
          </form>
        </div>
      ))}
    </div>
  );
}
