import React, { useEffect, useState } from 'react';
import './FacultyDashboard.css';
import UIDStatusList from '../components/UIDStatusList';


export default function FacultyDashboard() {
  const [activeSection, setActiveSection] = useState('request-uid');
  const [facultyDetails, setFacultyDetails] = useState(null);
  const [acceptanceLetter, setAcceptanceLetter] = useState(null);
  const [indexingProof, setIndexingProof] = useState(null);

  const [formValues, setFormValues] = useState({
    paperTitle: '',
    type: '',
    abstract: '',
    target: '',
    coAuthors: ''
  });

  const facultyId = localStorage.getItem('facultyId');

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/faculty/${facultyId}`);
        const data = await res.json();
        setFacultyDetails(data);
      } catch (err) {
        console.error('Error fetching faculty data:', err);
      }
    };

    if (facultyId) fetchDetails();
  }, [facultyId]);

  const handleFormChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const requestData = {
      facultyId: facultyDetails?.facultyId,
      facultyName: facultyDetails?.fullName,
      department: facultyDetails?.department,
      ...formValues
    };

    try {
      const res = await fetch('http://localhost:5000/api/hod/uid-request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });

      const data = await res.json();
      alert(data.message);

      setFormValues({
        paperTitle: '',
        type: '',
        abstract: '',
        target: '',
        coAuthors: ''
      });
    } catch (err) {
      console.error(err);
      alert('Submission failed');
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo-section">
          <img src="/svecw-logo.png" alt="SVECW Logo" className="logo" />
        </div>
        <nav className="menu">
          <p>Faculty Login</p>
          <ul>
            <li className={activeSection === 'request-uid' ? 'active' : ''} onClick={() => setActiveSection('request-uid')}>📄 Request UID</li>
            <li className={activeSection === 'uid-status' ? 'active' : ''} onClick={() => setActiveSection('uid-status')}>📘 UID Status</li>
            <li className={activeSection === 'submission' ? 'active' : ''} onClick={() => setActiveSection('submission')}>📝 Publication Submission</li>
            <li className={activeSection === 'indexing' ? 'active' : ''} onClick={() => setActiveSection('indexing')}>📤 Submit Indexing Proof and Acceptance letter</li>
            <li className={activeSection === 'my-submissions' ? 'active' : ''} onClick={() => setActiveSection('my-submissions')}>📂 My Submissions</li>
            <li className={activeSection === 'incentive' ? 'active' : ''} onClick={() => setActiveSection('incentive')}>💰 Incentive Status</li>
            <li className={activeSection === 'notifications' ? 'active' : ''} onClick={() => setActiveSection('notifications')}>🔔 Notifications</li>
            <li className={activeSection === 'summary' ? 'active' : ''} onClick={() => setActiveSection('summary')}>📊 Publication Summary</li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h3>Welcome, {facultyDetails?.fullName || 'Faculty'}</h3>

        <div className="summary-cards">
          <div className="card">2<br /><span>Approved PIDs</span></div>
          <div className="card">3<br /><span>Incentives Pending</span></div>
        </div>

        {/* UID Request Form */}
        {activeSection === 'request-uid' && (
          <div className="centered-form-container">
            <div className="uid-form-card">
              <h3>Request UID</h3>
              <form onSubmit={handleFormSubmit}>
                <input
                  type="text"
                  name="facultyName"
                  value={`Faculty Name: ${facultyDetails?.fullName || ''}`}
                  readOnly
                />
                <input
                  type="text"
                  name="facultyId"
                  value={`Faculty ID: ${facultyDetails?.facultyId || ''}`}
                  readOnly
                />
                <input
                  type="text"
                  name="department"
                  value={`Department: ${facultyDetails?.department || ''}`}
                  readOnly
                />

                <input
                  type="text"
                  name="paperTitle"
                  placeholder="Tentative Paper Title"
                  value={formValues.paperTitle}
                  onChange={handleFormChange}
                  required
                />
                <select
                  name="type"
                  value={formValues.type}
                  onChange={handleFormChange}
                  required
                >
                  <option value="">Type (Conference/Journal)</option>
                  <option value="Conference">Conference</option>
                  <option value="Journal">Journal</option>
                </select>
                <textarea
                  name="abstract"
                  placeholder="Abstract Space of 150 Words"
                  rows="3"
                  value={formValues.abstract}
                  onChange={handleFormChange}
                  required
                />
                <input
                  type="text"
                  name="target"
                  placeholder="Targeted Journal/ Conference Name"
                  value={formValues.target}
                  onChange={handleFormChange}
                  required
                />
                <input
                  type="text"
                  name="coAuthors"
                  placeholder="Co-Authors (if any)"
                  value={formValues.coAuthors}
                  onChange={handleFormChange}
                />
                <button type="submit">Submit to HoD</button>
              </form>
            </div>
          </div>
        )}

        {/* Other Sections */}
        {activeSection === 'uid-status' && (
  <div className="uid-status-section">
    <h3>Your UID Requests</h3>
    {!facultyDetails ? (
      <p>Loading...</p>
    ) : (
      <UIDStatusList facultyId={facultyDetails.facultyId} />
    )}
  </div>
)}


        {activeSection === 'submission' && <p>Publication Submission Component</p>}
        {activeSection === 'indexing' && (
  <div className="centered-form-container">
    <div className="uid-form-card">
      <h3>Upload Acceptance Letter & Indexing Proof</h3>
      <form
        onSubmit={async (e) => {
          e.preventDefault();
          const formData = new FormData();
          formData.append('facultyId', facultyDetails?.facultyId);
          if (acceptanceLetter) formData.append('acceptanceLetter', acceptanceLetter);
          if (indexingProof) formData.append('indexingProof', indexingProof);

          try {
            const res = await fetch('http://localhost:5000/api/faculty/upload-documents', {
              method: 'POST',
              body: formData
            });
            const data = await res.json();
            alert(data.message || 'Uploaded successfully');
            setAcceptanceLetter(null);
            setIndexingProof(null);
          } catch (err) {
            console.error(err);
            alert('Upload failed');
          }
        }}
      >
        <div className="form-group">
          <label>📄 Acceptance Letter (PDF/DOC)</label>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setAcceptanceLetter(e.target.files[0])}
            required
          />
        </div>

        <div className="form-group">
          <label>📑 Indexing Proof (PDF/DOC)</label>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setIndexingProof(e.target.files[0])}
            required
          />
        </div>

        <button type="submit">Submit Documents</button>
      </form>
    </div>
  </div>
)}

        {activeSection === 'my-submissions' && <p>My Submissions Component</p>}
        {activeSection === 'incentive' && <p>Incentive Status Component</p>}
        {activeSection === 'notifications' && <p>Notifications Component</p>}
        {activeSection === 'summary' && <p>Publication Summary Component</p>}
      </div>
    </div>
  );
}
