import React, { useEffect, useState } from 'react';
import './HodDashboard.css'; // Reuse styles from HoD dashboard

export default function AdminDashboard() {
  const [activeSection, setActiveSection] = useState('faculty-uid');
  const [approvedRequests, setApprovedRequests] = useState([]);
  const [submissions, setSubmissions] = useState([]);

useEffect(() => {
  if (activeSection === 'paper-submission') {
    fetch('http://localhost:5000/api/faculty/all-submitted-documents')
      .then(res => res.json())
      .then(data => setSubmissions(data))
      .catch(err => {
        console.error(err);
        alert('Failed to load document submissions.');
      });
  }
}, [activeSection]);

const handleDocumentDecision = async (id, action) => {
  try {
    let body = null;

    if (action === 'reject') {
      const reason = prompt('Enter reason for rejection:');
      if (!reason) return alert('Rejection reason required');
      body = JSON.stringify({ reason });
    }

    const res = await fetch(`http://localhost:5000/api/faculty/documents/${id}/${action}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body
    });

    const data = await res.json();
    alert(data.message);
    setSubmissions(prev => prev.filter(doc => doc._id !== id));
  } catch (err) {
    console.error(err);
    alert('Action failed');
  }
};

  // Load approved UID requests from HOD
  useEffect(() => {
    if (activeSection === 'faculty-uid') {
      fetch('http://localhost:5000/api/hod/uid-requests')
        .then(res => res.json())
        .then(data => {
          const filtered = data.filter(row => row.hodAccept === true && row.principalAccept === true && row.adminAccept!==true );
          setApprovedRequests(filtered);
        })
        .catch(err => {
          console.error(err);
          alert('Failed to load UID requests.');
        });
    }
  }, [activeSection]);

  // Handle Accept/Reject
  const handleAction = async (id, status) => {
  try {
    let body = null;

    if (status === 'reject') {
      const reason = prompt('Enter reason for rejection:');
      if (!reason || reason.trim() === '') {
        alert('Rejection reason is required.');
        return;
      }
      body = JSON.stringify({ reason });
    }

    const res = await fetch(`http://localhost:5000/api/admin/uid-request/${id}/${status}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body
    });

    const data = await res.json();
    alert(data.message);
    setApprovedRequests(prev => prev.filter(r => r._id !== id));
  } catch (err) {
    console.error(err);
    alert('Action failed');
  }
};


  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>R&D Admin</h3>
        <ul className="menu">
          <li className={activeSection === 'faculty-uid' ? 'active' : ''} onClick={() => setActiveSection('faculty-uid')}>
            üßæ Faculty UID Requests
          </li>
          <li className={activeSection === 'paper-submission' ? 'active' : ''} onClick={() => setActiveSection('paper-submission')}>
            üìù Documents Submissions
          </li>
          <li className={activeSection === 'approved-pids' ? 'active' : ''} onClick={() => setActiveSection('approved-pids')}>
            ‚úÖ Approved PIDs
          </li>
          <li className={activeSection === 'incentives' ? 'active' : ''} onClick={() => setActiveSection('incentives')}>
            üí∞ Incentives Pending
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'faculty-uid' && (
          <div className="uid-requests-container">
            <h2>UID Requests Approved by Principal</h2>
            {approvedRequests.length === 0 ? (
              <p>No UID requests pending approval.</p>
            ) : (
              approvedRequests.map(req => (
                <div className="uid-request-card" key={req._id}>
                  <h4>{req.paperTitle}</h4>
                  <p><strong>Faculty Name:</strong> {req.facultyName}</p>
                  <p><strong>Faculty ID:</strong> {req.facultyId}</p>
                  <p><strong>Department:</strong> {req.department}</p>
                  <p><strong>Type:</strong> {req.type}</p>
                  <p><strong>Target:</strong> {req.target}</p>
                  <p><strong>Abstract:</strong> {req.abstract}</p>
                  <p><strong>Co-Authors:</strong> {req.coAuthors}</p>
                  <p><strong>Submitted On:</strong> {new Date(req.submittedAt).toLocaleDateString()}</p>

                  <div className="actions">
                    <button className="accept" onClick={() => handleAction(req._id, 'accept')}>Accept</button>
                    <button className="reject" onClick={() => handleAction(req._id, 'reject')}>Reject</button>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {activeSection === 'paper-submission' && <p>Paper Submission Section</p>}
        {activeSection === 'approved-pids' && <p>Approved PIDs Section</p>}
        {activeSection === 'incentives' && <p>Incentives Pending Section</p>}
      </div>
    </div>
  );
}
