import React, { useState } from 'react';
import AddFacultyForm from './AddFacultyForm';
import RemoveFaculty from './RemoveFaculty';
import './MainAdminDashboard.css';

const MainAdminDashboard = () => {
  const [activeSection, setActiveSection] = useState('add');

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <aside className="sidebar">
        <h2 className="sidebar-title">Admin Dashboard</h2>
        <button
          onClick={() => setActiveSection('add')}
          className={`sidebar-btn ${activeSection === 'add' ? 'active' : ''}`}
        >
          ➕ Add Faculty
        </button>
        <button
          onClick={() => setActiveSection('remove')}
          className={`sidebar-btn ${activeSection === 'remove' ? 'active' : ''}`}
        >
          ❌ Remove Faculty
        </button>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        {activeSection === 'add' && <AddFacultyForm />}
        {activeSection === 'remove' && <RemoveFaculty />}
      </main>
    </div>
  );
};

export default MainAdminDashboard;
