import React, { useState } from 'react';

const AddFacultyForm = () => {
  const [form, setForm] = useState({
    fullName: '',
    email: '',
    password: '',
    facultyId: '',
    phoneNumber: '',
    gender: '',
    department: ''
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    // API Call here
    alert('Faculty added (mock)');
  };

  return (
    <div className="bg-white p-6 rounded shadow-md w-full max-w-2xl">
      <h3 className="text-xl font-semibold mb-4">Add Faculty</h3>
      <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4">
        <input type="text" name="fullName" placeholder="Full Name" value={form.fullName} onChange={handleChange} className="p-2 border rounded" required />
        <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} className="p-2 border rounded" required />
        <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} className="p-2 border rounded" required />
        <input type="text" name="facultyId" placeholder="Faculty ID" value={form.facultyId} onChange={handleChange} className="p-2 border rounded" required />
        <input type="text" name="phoneNumber" placeholder="Phone Number" value={form.phoneNumber} onChange={handleChange} className="p-2 border rounded" />
        <select name="gender" value={form.gender} onChange={handleChange} className="p-2 border rounded">
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>
        <input type="text" name="department" placeholder="Department" value={form.department} onChange={handleChange} className="p-2 border rounded" required />

        <button type="submit" className="col-span-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddFacultyForm;
