// src/pages/HodDashboard.js
import React, { useState } from 'react';
import './HodDashboard.css';
import UIDRequestsPage from './UIDRequestsPage';
import logo from './logo2.jpeg';

export default function HodDashboard() {
  const [activeSection, setActiveSection] = useState('requests'); // Default section

  const handleNavigation = (section) => {
    if (section === 'logout') {
      // Perform logout logic here (e.g., clear auth tokens, redirect to login)
      alert('Logging out...');
      // For example: window.location.href = '/login';
      return;
    }
    setActiveSection(section);
  };

  return (
    <div className="hod-dashboard">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h2>HOD Dashboard</h2>
        <ul>
          <li
            className={activeSection === 'requests' ? 'active' : ''}
            onClick={() => handleNavigation('requests')}
          >
            ðŸ“¨ Requesting UIDs
          </li>
          <li
            className={activeSection === 'profile' ? 'active' : ''}
            onClick={() => handleNavigation('profile')}
          >
            ðŸ‘¤ Profile
          </li>
          <li
            onClick={() => handleNavigation('logout')}
            style={{ cursor: 'pointer', color: '#dc3545', marginTop: 'auto' }}
          >
            ðŸšª Logout
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'requests' && <UIDRequestsPage />}
        {activeSection === 'profile' && (
          <div>
            <h2>Profile</h2>
            <p>This is the profile section. Add your profile details here.</p>
          </div>
        )}
      </div>
    </div>
  );
}
