import React, { useState } from 'react';
import './FacultyDashboard.css';

export default function FacultyDashboard() {
  const [activeSection, setActiveSection] = useState('request-uid');

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo-section">
          <img src="/svecw-logo.png" alt="SVECW Logo" className="logo" />
        </div>
        <nav className="menu">
          <p>Faculty Login</p>
          <ul>
            <li
              className={activeSection === 'request-uid' ? 'active' : ''}
              onClick={() => setActiveSection('request-uid')}
            >
              üìÑ Request UID
            </li>
            <li
              className={activeSection === 'uid-status' ? 'active' : ''}
              onClick={() => setActiveSection('uid-status')}
            >
              üìò UID Status
            </li>
            <li
              className={activeSection === 'submission' ? 'active' : ''}
              onClick={() => setActiveSection('submission')}
            >
              üìù Publication Submission
            </li>
            <li
              className={activeSection === 'indexing' ? 'active' : ''}
              onClick={() => setActiveSection('indexing')}
            >
              üì§ Submit Indexing Proof
            </li>
            <li
              className={activeSection === 'my-submissions' ? 'active' : ''}
              onClick={() => setActiveSection('my-submissions')}
            >
              üìÇ My Submissions
            </li>
            <li
              className={activeSection === 'incentive' ? 'active' : ''}
              onClick={() => setActiveSection('incentive')}
            >
              üí∞ Incentive Status
            </li>
            <li
              className={activeSection === 'notifications' ? 'active' : ''}
              onClick={() => setActiveSection('notifications')}
            >
              üîî Notifications
            </li>
            <li
              className={activeSection === 'summary' ? 'active' : ''}
              onClick={() => setActiveSection('summary')}
            >
              üìä Publication Summary
            </li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h2>Faculty login Dashboard</h2>
        <h3>Welcome, Faculty Name</h3>

        <div className="summary-cards">
          <div className="card">2<br /><span>Approved PIDs</span></div>
          <div className="card">3<br /><span>Incentives Pending</span></div>
        </div>

        {/* Conditional Section Rendering */}
        

        {/* Placeholder for other sections */}
        {activeSection === 'uid-status' && <p>UID Status Component</p>}
        {activeSection === 'submission' && <p>Publication Submission Component</p>}
        {activeSection === 'indexing' && <p>Submit Indexing Proof Component</p>}
        {activeSection === 'my-submissions' && <p>My Submissions Component</p>}
        {activeSection === 'incentive' && <p>Incentive Status Component</p>}
        {activeSection === 'notifications' && <p>Notifications Component</p>}
        {activeSection === 'summary' && <p>Publication Summary Component</p>}
      </div>
    </div>
  );
}
