// src/components/UIDStatusList.js
import React, { useEffect, useState } from 'react';
import './UIDStatusList.css'; // Optional for custom styles

export default function UIDStatusList({ facultyId }) {
  const [uidRequests, setUidRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/api/faculty/uid-requests/${facultyId}`)
      .then(res => res.json())
      .then(data => {
        setUidRequests(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching UID status:', err);
        setLoading(false);
      });
  }, [facultyId]);

  if (loading) return <p>Loading...</p>;

  if (uidRequests.length === 0) return <p>No UID requests submitted yet.</p>;

  return (
    <div className="uid-requests-container">
      {uidRequests.map((req) => (
        <div className="uid-request-card" key={req._id}>
          <h4>{req.paperTitle}</h4>
          <p><strong>Submitted On:</strong> {new Date(req.submittedAt).toLocaleDateString()}</p>
          <p><strong>Type:</strong> {req.type}</p>
          <p><strong>Target:</strong> {req.target}</p>
          <p><strong>Co-Authors:</strong> {req.coAuthors}</p>
          <p><strong>Abstract:</strong> {req.abstract}</p>

          <hr />
          <p><strong>HoD Approval:</strong> {req.hodAccept ? '✅ Approved' : '⏳ Pending'}</p>
          <p><strong>Principal Approval:</strong> {req.principalAccept ? '✅ Approved' : '⏳ Pending'}</p>
          <p><strong>Admin Approval:</strong> {req.adminAccept ? '✅ Approved' : '⏳ Pending'}</p>
          <p><strong>Final UID:</strong> {req.uid || '⏳ Not generated yet'}</p>
        </div>
      ))}
    </div>
  );
}
