import React, { useEffect, useState } from 'react';

export default function PIDStatusList({ facultyId }) {
  const [statusData, setStatusData] = useState({ approved: [], rejected: [], pending: [] });

  useEffect(() => {
    const fetchStatus = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/faculty/pid-status/${facultyId}`);
        const data = await res.json();
        setStatusData(data);
      } catch (err) {
        console.error('Error fetching PID status:', err);
      }
    };

    if (facultyId) fetchStatus();
  }, [facultyId]);

  const renderSection = (title, data, color) => (
    <div className="pid-status-section">
      <h4 style={{ color }}>{title} ({data.length})</h4>
      {data.length === 0 ? <p>No entries.</p> : (
        data.map((doc) => (
          <div key={doc._id} className="pid-card">
            <p><strong>UID:</strong> {doc.uid}</p>
            <p><strong>Paper Title:</strong> {doc.paperTitle}</p>
            <p><strong>Type:</strong> {doc.type}</p>
            <p><strong>Target:</strong> {doc.target}</p>
          </div>
        ))
      )}
    </div>
  );

  return (
    <div className="pid-status-list">
      {renderSection('‚úÖ Approved', statusData.approved, 'green')}
      {renderSection('‚ùå Rejected', statusData.rejected, 'red')}
      {renderSection('üïì Pending', statusData.pending, 'orange')}
    </div>
  );
}
