import React, { useEffect, useState } from 'react';
import './FacultyDashboard.css';
import UIDStatusList from '../components/UIDStatusList';

export default function FacultyDashboard() {
  const [activeSection, setActiveSection] = useState('request-uid');
  const [facultyDetails, setFacultyDetails] = useState(null);
  const [formValues, setFormValues] = useState({
    paperTitle: '',
    type: '',
    abstract: '',
    target: '',
    coAuthors: ''
  });
  const [approvedUIDs, setApprovedUIDs] = useState([]);
  const [documents, setDocuments] = useState({});
  const [mySubmissions, setMySubmissions] = useState([]);

  const facultyId = localStorage.getItem('facultyId');

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/faculty/${facultyId}`);
        const data = await res.json();
        setFacultyDetails(data);
      } catch (err) {
        console.error('Error fetching faculty data:', err);
      }
    };
    if (facultyId) fetchDetails();
  }, [facultyId]);

  useEffect(() => {
    const fetchApprovedUIDs = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/faculty/approved-uid-requests/${facultyId}`);
        const data = await res.json();
        setApprovedUIDs(data);
      } catch (err) {
        console.error('Error fetching approved UID requests:', err);
      }
    };
    if (activeSection === 'indexing') fetchApprovedUIDs();
  }, [activeSection, facultyId]);

  useEffect(() => {
    const fetchSubmissions = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/faculty/my-submissions/${facultyId}`);
        const data = await res.json();
        if (Array.isArray(data)) setMySubmissions(data);
        else setMySubmissions([]);
      } catch (err) {
        console.error('Error fetching submissions:', err);
        setMySubmissions([]);
      }
    };
    if (activeSection === 'my-submissions') fetchSubmissions();
  }, [activeSection, facultyId]);

  const handleFormChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const requestData = {
      facultyId: facultyDetails?.facultyId,
      facultyName: facultyDetails?.fullName,
      department: facultyDetails?.department,
      ...formValues
    };
    try {
      const res = await fetch('http://localhost:5000/api/hod/uid-request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });
      const data = await res.json();
      alert(data.message);
      setFormValues({ paperTitle: '', type: '', abstract: '', target: '', coAuthors: '' });
    } catch (err) {
      console.error(err);
      alert('Submission failed');
    }
  };

  const handleFileChange = (uid, field, file) => {
    setDocuments(prev => ({ ...prev, [uid]: { ...prev[uid], [field]: file } }));
  };

  const handleDocumentUpload = async (uid) => {
    const formData = new FormData();
    formData.append('facultyId', facultyId);
    formData.append('uid', uid);
    if (documents[uid]?.acceptanceLetter) {
      formData.append('acceptanceLetter', documents[uid].acceptanceLetter);
    }
    if (documents[uid]?.indexingProof) {
      formData.append('indexingProof', documents[uid].indexingProof);
    }
    try {
      const res = await fetch('http://localhost:5000/api/faculty/upload-documents', {
        method: 'POST',
        body: formData
      });
      const data = await res.json();
      alert(data.message || 'Uploaded successfully');
      setApprovedUIDs(prev => prev.filter(request => request.uid !== uid));
      setDocuments(prev => ({ ...prev, [uid]: { acceptanceLetter: null, indexingProof: null } }));
    } catch (err) {
      console.error(err);
      alert('Upload failed');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <div className="logo-section">
          <img src="/svecw-logo.png" alt="SVECW Logo" className="logo" />
        </div>
        <nav className="menu">
          <p>Faculty Login</p>
          <ul>
            <li className={activeSection === 'request-uid' ? 'active' : ''} onClick={() => setActiveSection('request-uid')}>üìÑ Request UID</li>
            <li className={activeSection === 'uid-status' ? 'active' : ''} onClick={() => setActiveSection('uid-status')}>üìò UID Status</li>
            <li className={activeSection === 'submission' ? 'active' : ''} onClick={() => setActiveSection('submission')}>üìù Publication Submission</li>
            <li className={activeSection === 'indexing' ? 'active' : ''} onClick={() => setActiveSection('indexing')}>üì§ Submit Documents</li>
            <li className={activeSection === 'my-submissions' ? 'active' : ''} onClick={() => setActiveSection('my-submissions')}>üìÇ My Submissions</li>
            <li className={activeSection === 'incentive' ? 'active' : ''} onClick={() => setActiveSection('incentive')}>üí∞ Incentive Status</li>
            <li className={activeSection === 'notifications' ? 'active' : ''} onClick={() => setActiveSection('notifications')}>üîî Notifications</li>
            <li className={activeSection === 'summary' ? 'active' : ''} onClick={() => setActiveSection('summary')}>üìä Publication Summary</li>
          </ul>
        </nav>
      </div>

      <div className="main-content">
        <h3>Welcome, {facultyDetails?.fullName || 'Faculty'}</h3>

        {activeSection === 'request-uid' && (
          <div className="centered-form-container">
            <form onSubmit={handleFormSubmit} className="uid-form-card">
              <h3>Request UID</h3>
              <input type="text" value={`Faculty Name: ${facultyDetails?.fullName || ''}`} readOnly />
              <input type="text" value={`Faculty ID: ${facultyDetails?.facultyId || ''}`} readOnly />
              <input type="text" value={`Department: ${facultyDetails?.department || ''}`} readOnly />
              <input type="text" name="paperTitle" placeholder="Tentative Paper Title" value={formValues.paperTitle} onChange={handleFormChange} required />
              <select name="type" value={formValues.type} onChange={handleFormChange} required>
                <option value="">Type (Conference/Journal)</option>
                <option value="Conference">Conference</option>
                <option value="Journal">Journal</option>
              </select>
              <textarea name="abstract" placeholder="Abstract (max 150 words)" rows="3" value={formValues.abstract} onChange={handleFormChange} required />
              <input type="text" name="target" placeholder="Target Journal/Conference" value={formValues.target} onChange={handleFormChange} required />
              <input type="text" name="coAuthors" placeholder="Co-Authors (if any)" value={formValues.coAuthors} onChange={handleFormChange} />
              <button type="submit">Submit to HoD</button>
            </form>
          </div>
        )}

        {activeSection === 'uid-status' && (
          <div className="uid-status-section">
            <h3>Your UID Requests</h3>
            {!facultyDetails ? <p>Loading...</p> : <UIDStatusList facultyId={facultyDetails.facultyId} />}
          </div>
        )}

        {activeSection === 'indexing' && (
          <div className="approved-uid-section">
            <h3>Upload Documents for Approved UID Requests</h3>
            {approvedUIDs.length === 0 ? <p>No approved UID requests found.</p> : (
              approvedUIDs.map((uid) => (
                <div key={uid._id} className="uid-card">
                  <p><strong>UID:</strong> {uid.uid}</p>
                  <p><strong>Paper Title:</strong> {uid.paperTitle}</p>
                  <p><strong>Type:</strong> {uid.type}</p>
                  <p><strong>Target:</strong> {uid.target}</p>
                  <div className="form-group">
                    <label>üìÑ Acceptance Letter</label>
                    <input type="file" accept=".pdf,.doc,.docx" onChange={(e) => handleFileChange(uid.uid, 'acceptanceLetter', e.target.files[0])} required />
                  </div>
                  <div className="form-group">
                    <label>üìë Indexing Proof</label>
                    <input type="file" accept=".pdf,.doc,.docx" onChange={(e) => handleFileChange(uid.uid, 'indexingProof', e.target.files[0])} required />
                  </div>
                  <button onClick={() => handleDocumentUpload(uid.uid)}>Submit Documents</button>
                </div>
              ))
            )}
          </div>
        )}

        {activeSection === 'my-submissions' && (
          <div className="submission-list">
            <h3>My Submissions</h3>
            {mySubmissions.length === 0 ? <p>No submissions found.</p> : (
              mySubmissions.map((submission) => (
                <div key={submission._id} className="uid-card">
                  <p><strong>UID:</strong> {submission.uid}</p>
                  <p><strong>Uploaded At:</strong> {new Date(submission.uploadedAt).toLocaleDateString()}</p>
                  <p><strong>Acceptance Letter:</strong> {submission.acceptanceLetter.filename}</p>
                  <a href={`data:${submission.acceptanceLetter.contentType};base64,${submission.acceptanceLetter.base64}`} download={submission.acceptanceLetter.filename}>üì• Download Acceptance Letter</a>
                  <p><strong>Indexing Proof:</strong> {submission.indexingProof.filename}</p>
                  <a href={`data:${submission.indexingProof.contentType};base64,${submission.indexingProof.base64}`} download={submission.indexingProof.filename}>üì• Download Indexing Proof</a>
                </div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
}
