// src/pages/UIDRequestsPage.js
import React, { useEffect, useState } from 'react';
import './HodDashboard.css'; // Import shared CSS

export default function UIDRequestsPage() {
  const [requests, setRequests] = useState([]);

  // Fetch UID requests on load
  useEffect(() => {
    fetch('http://localhost:5000/api/hod/uid-requests')
      .then(res => res.json())
      .then(data => setRequests(data))
      .catch(err => {
        console.error('Error fetching UID requests:', err);
        alert('Failed to load UID requests.');
      });
  }, []);

  // Handle Accept/Reject
  const handleAction = async (id, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/hod/uid-request/${id}/${status}`, {
        method: 'PUT'
      });
      const data = await res.json();
      alert(data.message);
      // Refresh list
      setRequests(prev => prev.filter(r => r._id !== id));
    } catch (err) {
      console.error(err);
      alert('Action failed');
    }
  };

  return (
    <div className="uid-requests-container">
      <h2>Pending UID Requests</h2>

      {requests.length === 0 ? (
        <p>No UID requests pending.</p>
      ) : (
        requests.map(req => (
          <div className="uid-request-card" key={req._id}>
            <h4>{req.paperTitle}</h4>
            <p><strong>Faculty Name:</strong> {req.facultyName}</p>
            <p><strong>Faculty ID:</strong> {req.facultyId}</p>
            <p><strong>Department:</strong> {req.department}</p>
            <p><strong>Type:</strong> {req.type}</p>
            <p><strong>Target:</strong> {req.target}</p>
            <p><strong>Abstract:</strong> {req.abstract}</p>
            <p><strong>Co-Authors:</strong> {req.coAuthors}</p>

            <div className="actions">
              <button className="accept" onClick={() => handleAction(req._id, 'accept')}>Accept</button>
              <button className="reject" onClick={() => handleAction(req._id, 'reject')}>Reject</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
