// src/pages/UIDRequestsPage.js
import React, { useEffect, useState } from 'react';
import './HodDashboard.css';

export default function UIDRequestsPage() {
  const [requests, setRequests] = useState([]);
  const [department, setDepartment] = useState('');

  const hodId = localStorage.getItem('hodId');

  useEffect(() => {
    const fetchHodDetailsAndRequests = async () => {
      try {
        // Step 1: Get HoD details
        const resHod = await fetch(`http://localhost:5000/api/hod/${hodId}`);
        const hodData = await resHod.json();
        setDepartment(hodData.department);

        // Step 2: Get all UID requests
        const resRequests = await fetch('http://localhost:5000/api/hod/uid-requests');
        const allRequests = await resRequests.json();

        // Step 3: Filter by HoD department
        const filtered = allRequests.filter(req => req.department === hodData.department);
        setRequests(filtered);
      } catch (err) {
        console.error('Error loading UID requests or HoD info:', err);
        alert('Failed to load UID requests.');
      }
    };

    if (hodId) {
      fetchHodDetailsAndRequests();
    }
  }, [hodId]);

  const handleAction = async (id, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/hod/uid-request/${id}/${status}`, {
        method: 'PUT'
      });
      const data = await res.json();
      alert(data.message);
      setRequests(prev => prev.filter(r => r._id !== id));
    } catch (err) {
      console.error(err);
      alert('Action failed');
    }
  };

  return (
    <div className="uid-requests-container">
      <h2>Pending UID Requests ({department})</h2>

      {requests.length === 0 ? (
        <p>No UID requests pending.</p>
      ) : (
        requests.map(req => (
          <div className="uid-request-card" key={req._id}>
            <h4>{req.paperTitle}</h4>
            <p><strong>Faculty Name:</strong> {req.facultyName}</p>
            <p><strong>Faculty ID:</strong> {req.facultyId}</p>
            <p><strong>Department:</strong> {req.department}</p>
            <p><strong>Type:</strong> {req.type}</p>
            <p><strong>Target:</strong> {req.target}</p>
            <p><strong>Abstract:</strong> {req.abstract}</p>
            <p><strong>Co-Authors:</strong> {req.coAuthors}</p>

            <div className="actions">
              <button className="accept" onClick={() => handleAction(req._id, 'accept')}>Accept</button>
              <button className="reject" onClick={() => handleAction(req._id, 'reject')}>Reject</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
