// src/pages/HodDashboard.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './HodDashboard.css';
import UIDRequestsPage from './UIDRequestsPage';
import logo from './logo2.jpeg';

export default function HodDashboard() {
  const [activeSection, setActiveSection] = useState('requests');
  const navigate = useNavigate();

  // Mock HOD profile data â€” replace with real data fetch or context
  const hodProfile = {
    name: 'Dr. Rajesh Kumar',
    department: 'Computer Science',
    email: 'rajesh.kumar@example.edu',
    phone: '+91 98765 43210',
    employeeId: 'HOD1234',
  };

  const handleNavigation = (section) => {
    if (section === 'logout') {
      // Perform logout logic here like clearing tokens, then redirect
      // For now just redirect
      navigate('/hod-login');
      return;
    }
    setActiveSection(section);
  };

  return (
    <div className="hod-dashboard">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h2>HOD Dashboard</h2>
        <ul>
          <li
            className={activeSection === 'requests' ? 'active' : ''}
            onClick={() => handleNavigation('requests')}
          >
            ðŸ“¨ Requesting UIDs
          </li>
          <li
            className={activeSection === 'profile' ? 'active' : ''}
            onClick={() => handleNavigation('profile')}
          >
            ðŸ‘¤ Profile
          </li>
          <li
            onClick={() => handleNavigation('logout')}
            style={{ cursor: 'pointer', color: 'white', marginTop: 'auto' }}
          >
            ðŸšª Logout
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'requests' && <UIDRequestsPage />}
        {activeSection === 'profile' && (
          <div className="profile-section">
            <h2>HOD Profile</h2>
            <div className="profile-card">
              <p><strong>Name:</strong> {hodProfile.name}</p>
              <p><strong>Department:</strong> {hodProfile.department}</p>
              <p><strong>Email:</strong> {hodProfile.email}</p>
              <p><strong>Phone:</strong> {hodProfile.phone}</p>
              <p><strong>Employee ID:</strong> {hodProfile.employeeId}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
